import React, {useState} from 'react';
import './App.css';

export default function Chat() {
  const initialChatMessage = {
    message: 'Welcome to Robertoyoc.com, send us your message',
    from: 'chatbox'
  }
  const [message, setMessage] = useState('');
  const [messages, addMessage] = useState([initialChatMessage]); 

  function postMessage() {
    if(message === '') {
      alert('Message should not be empty');
    } else {
      messages.push(
        {
          message: message,
          from: 'user'
        }
      );
      console.log(messages);
      setMessage('');
    }
  }

  return (
    <div className="chat">
      <div className="main">
        <div className="messages">
          {messages.map((message, index) => {
            return (
              <div key={index} className={message.from === 'user' ? 'from-user': 'from-chatbox'}>
                <p className={message.from === 'user' ? 'user-msg': 'chatbot-msg'}>{message.message}</p>
              </div>
            );
          })}
        </div>
        <div className="send">
          <input type="text" value={message} placeholder="Type your message here" onChange={(e) => setMessage(e.target.value)} onKeyDown={(e) => setMessage(e.target.value)} />
          <button onClick={postMessage}><i className="material-icons">send</i></button>
        </div>
      </div>
    </div>
  );
}
