import react, {Component} from 'react';
import {useAuth0} from '@auth0/auth0-react';

export default class App extends Component {
  constructor(props) {
    super(props);
  }

  componentDidMount() {
    console.log(useAuth0());
  }

  profile = () => {
    const {user, isAuthentcated, isLoading} = useAuth0();

    if(isAuthentcated) {
      console.log(user);
    }

    return isAuthentcated ? (<h1>Welcome</h1>): (<LoginButton />);
  }

  LoginButton = () => {
    const {loginWithRedirect} = useAuth0();

    return(
      <button className="login-button" onClick={() => loginWithRedirect()}>Login</button>
    )
  }

  LogoutButton = () => {
    const {logout} = useAuth0();

    return(
      <button className="login-button" onClick={() => logout({returnTo: window.location.origin})}>Logout</button>
    );
  }

  render() {
    const {user, isAuthentcated, isLoading} = useAuth0();
    return(
      <div className="main">
        <div className="card">
          <h3>Welcome to robertoyoc.com</h3>
          <span className="subtitle-text">Please LogIn here:</span>
        </div>
      </div>
    );
  }
}
