import React, {useState} from 'react';
import './App.css';
const axios = require('axios');

export default function Chat() {
  const [message, setMessage] = useState('');
  const [messages, addMessage] = useState([]); 

  function postMessage() {
    if(message === '') {
      alert('Message should not be empty');
    } else {
      messages.push(
        {
          message: message,
          from: 'user'
        }
      );
      setMessage('');
    }
    axios.post('http://127.0.0.1:5002/getMessage', {
      message: message
    })
    .then (res => {
      messages.push({
        message: res.text,
        from: 'chatbot'
      })
    })
    .catch(err => {
      console.log(err);
    });

  }

  return (
    <div className="chat">
      <div className="main">
        <div className="messages">
          {messages.map((message, index) => {
            return (
              <div key={index} className={message.from === 'user' ? 'from-user': 'from-chatbox'}>
                {message.from === 'user' && <p className={message.from === 'user' ? 'user-msg': 'chatbot-msg'}>{message.message}</p>}
                {message.from === 'chatbot' && message.message}
              </div>
            );
          })}
        </div>
        <div className="send">
          <input type="text" value={message} placeholder="Type your message here" onChange={(e) => setMessage(e.target.value)} />
          <button onClick={postMessage}><i className="material-icons">send</i></button>
        </div>
      </div>
    </div>
  );
}
